Properties.commandText:
INSERT INTO "Users" ("PasswordHash", "Role", "Username")
VALUES (@p0, @p1, @p2)
RETURNING "ID";

INSERT INTO "RefreshTokens" ("ExpiresAt", "IssuedAt", "Token", "UserID")
VALUES (@p0, @p1, @p2, @p3)
RETURNING "ID";

INSERT INTO "Chats" ("Name", "OwnerID")
VALUES (@p0, @p1)
RETURNING "ID";

INSERT INTO "Messages" ("ChatID", "DestinationID", "OriginID", "Text", "Timestamp", "chatID", "isRead")
VALUES (@p0, @p1, @p2, @p3, @p4, @p5, @p6)
RETURNING "ID";

INSERT INTO "MessageReadUsers" ("MessageId", "UserId")
VALUES (@p0, @p1);
UPDATE "Messages" SET "isRead" = @p2
WHERE "ID" = @p3;
